import React from 'react';
import ReactDOM from 'react-dom';

function App() {
  return (
    <>
   <Square value={1} />
   <Square value={2} />
   </>
  );
}


class Board extends React.Component {
  renderSquare(i) {
    return <Square value={i} />;  }
}
class Square extends React.Component {
  render() {
    return (
      <button className="square">
        {this.props.value}      </button>
    );
  }
}

export default App;
