import React from 'react';
import ReactDOM from 'react-dom';

function App() {
  return (
    <>
   <Board  />
   </>
  );
}


class Board extends React.Component {
  renderSquare(i) {
    return <Square value={i} />;  }

  render(){

    return(

      <div>
      <div className='row'>
      {this.renderSquare(1)}
      {this.renderSquare(2)}
      {this.renderSquare(3)}
      </div>
      <div className='row'>
      {this.renderSquare(4)}
      {this.renderSquare(5)}
      {this.renderSquare(6)}
      </div>
      <div className='row'>
      {this.renderSquare(7)}
      {this.renderSquare(8)}
      {this.renderSquare(9)}
      </div>
      </div>


    );

  }
}
class Square extends React.Component {
  render() {
    return (
      <button className="square">
        {this.props.value}      </button>
    );
  }
}

export default App;
