import React from 'react';
import ReactDOM from 'react-dom';

function App() {
  return (
    <>
   <Board  />
   </>
  );
}


class Board extends React.Component {
  renderSquare(i,txt) {
    return <Square value={i} txt={txt}/>;  }

  render(){

    return(

      <div>
      <div className='row'>
      {this.renderSquare('1ABC')}
      {this.renderSquare(2)}
      {this.renderSquare(3)}
      </div>
      <div className='row'>
      {this.renderSquare(4)}
      {this.renderSquare(5)}
      {this.renderSquare(6)}
      </div>
      <div className='row'>
      {this.renderSquare(7)}
      {this.renderSquare(8)}
      {this.renderSquare(9)}
      </div>
      </div>


    );

  }
}
class Square extends React.Component {
  constructor(props){

    super(props);
    this.state = {

      txt_str:null,
      value:null
    };


  }
  render() {
    return (
      <button className="square" onClick={()=>console.log(this.props.value)}>
        <div className='top'>
        {this.props.value} 
        </div>
        <div className='bot'>
        {this.props.txt} 
        </div>   
      </button>
    );
  }
}

export default App;
